package graphene.model.graphserver;

import graphene.model.graph.DirectableObject;
import graphene.model.view.entities.IdProperty;

import java.util.ArrayList;
import java.util.List;
/**
 * XXX: Why are we using custom hashcode and equals?  Use autogenerated ones.
 * @author ??
 *
 */
public class TransactionDistinctEntityPair implements DirectableObject {

	private List<IdProperty> attributeList = new ArrayList<IdProperty>();
	private String dest = "";
	private String destEntityId = "";
	private String destEntityName = "";
	private int nbr = 0;
	private String src = "";

	private String srcEntityId = "";
	private String srcEntityName = "";

	private Double total = 0D;

	private String value = "";

	private int weight = 0;

	public TransactionDistinctEntityPair() {

	}

	public void addAttribute(IdProperty attr) {
		attributeList.add(attr);
	}

	/**
	 * This was changed so it overrides the equals(Object) instead of inheriting
	 * it as a covariant of equals(TransactionDistinctEntityPair). This was
	 * labeled by findbugs as scary. --djue
	 */
	public boolean equals(Object o) {
		if (o instanceof TransactionDistinctEntityPair) {
			TransactionDistinctEntityPair t = (TransactionDistinctEntityPair) o;
			if (!src.equals(t.getSrc()))
				return false;
			if (!dest.equals(t.getDest()))
				return false;
			return true;
		}
		return false;
	}

	public List<IdProperty> getAttributeList() {
		return attributeList;
	}

	@Override
	public String getDest() {
		// TODO Auto-generated method stub
		return dest;
	}

	@Override
	public List<IdProperty> getDestAttributes() {
		// TODO Auto-generated method stub
		return null;
	}

	public String getDestEntityId() {
		return destEntityId;
	}

	public String getDestEntityName() {
		return destEntityName;
	}

	@Override
	public List<IdProperty> getEdgeAttributes() {
		return attributeList;
	}

	@Override
	public String getId() {
		// TODO Auto-generated method stub
		return null;
	}

	public int getNbr() {
		return nbr;
	}

	@Override
	public String getSrc() {
		return src;
	}

	@Override
	public List<IdProperty> getSrcAttributes() {
		// TODO Auto-generated method stub
		return null;
	}

	public String getSrcEntityId() {
		return srcEntityId;
	}

	public String getSrcEntityName() {
		return srcEntityName;
	}

	public Double getTotal() {
		return total;
	}

	@Override
	public String getValue() {
		return value;
	}

	@Override
	public int getWeight() {
		return weight;
	}

	public int hashCode() {
		return (src + dest).hashCode();
	}

	public void setAttributeList(List<IdProperty> attributeList) {
		this.attributeList = attributeList;
	}

	public void setDest(String dest) {
		this.dest = dest;
	}

	public void setDestEntityId(String destEntityId) {
		this.destEntityId = destEntityId;
	}

	public void setDestEntityName(String destEntityName) {
		this.destEntityName = destEntityName;
	}

	public void setNbr(int nbr) {
		this.nbr = nbr;
	}

	public void setSrc(String src) {
		this.src = src;
	}

	public void setSrcEntityId(String srcEntityId) {
		this.srcEntityId = srcEntityId;
	}

	public void setSrcEntityName(String srcEntityName) {
		this.srcEntityName = srcEntityName;
	}

	public void setTotal(Double total) {
		this.total = total;
	}

	public void setValue(String value) {
		this.value = value;
	}

	public void setWeight(int weight) {
		this.weight = weight;
	}

}
